(function ($) {
    'use strict';
    $(function () {
        $('#edit_home').avgrund({
            height: 700,
            holderClass: 'custom',
            showClose: true,
            showCloseText: 'x',
            onBlurContainer: '.container-scroller',
            template: '<h6>Edit Home View Text</h6>' +
                '<div>' +
                '<form>' +
                '<div class="mt-4">' +
                '<input class="form-control" type="text" placeholder="Add a Title"/>' +
                '</div>' +
                '<!-- <div class="mt-4">' +
                '<div class="form-group">' +
                '<input type="file" name="thumbnail" accept="image/*" id="id_thumbnail" class="file-upload-default">' +
                '<div class="input-group col-xs-12">' +
                '<input type="text" class="form-control file-upload-info" disabled placeholder="Upload Image">' +
                '<span class="input-group-append">' +
                '<button class="file-upload-browse btn btn-gradient-primary" type="button" style="background:#62bdfc;">Upload</button>' +
                '</span>' +
                '</div>' +
                '</div> -->' +
                '<div class = "mt-4" > ' +
                '<input type="file" name="thumbnail" placeholder="Upload" accept="image/*" class="file-upload-default">' +
                '</div> ' +
                '<div class="mt-4">' +
                '<textarea class="form-control" placeholder="Text Content ( Best to keep it short and simple. ) "></textarea>' +
                '</div>' +
                '<div class="row justify-content-center">' +
                '<div class="text-center mt-4 mr-5">' +
                '<button type="submit" style="background:#62bdfc;" class="btn btn-success mr-2">Add</button>' +
                '</div>' +
                '<div class="text-center mt-4 ">' +
                '<a href="#" class="btn btn-light">Cancel</a>' +
                '</div>' +
                '</div>' +
                '</form>' +
                '</div>'

        });
    })
})(jQuery);


